mapper.update:
update CONSIGNMENTS
SET  
	P_STATUS = '8796105375835',  
	P_DELIVERYPOINTOFSERVICE = #{storeId}
WHERE 
	P_CODE = #{orderCode}

mapper.select:
select C.PK, C.P_STATUS, C.P_ALLOCATIONEMPLOYEECODE as P_ALLOCATIONEMPLOYEECODE, C.P_SHIPPINGADDRESS as P_SHIPPINGADDRESS, O.P_CODE, O.P_BUYERNICK as P_BUYERNICK, O2O.P_RECOMMENDSTORE, O2O.P_CUSCOMMET, O2O.P_SERVERCOMMENT, O2O.P_AGENCYCOMMENT, P.P_CODE as P_ICODE, P.P_CATEGORY as P_CATEGORY, OE.P_QUANTITY as P_QUANTITY, OE.P_BASEPRICE as P_BASEPRICE, OE.P_TOTALPRICE as P_TOTALPRICE, ROWNUM RN from CONSIGNMENTS C LEFT JOIN ORDERS O ON O.PK=C.P_ORDER LEFT JOIN O2OCONSIGNMENTITEMS O2O ON C.PK=O2O.P_CONSIGNMENT LEFT JOIN ORDERENTRIES OE ON OE.P_ORDER=O.PK LEFT JOIN PRODUCTS P ON OE.P_PRODUCT=P.PK where 1= 1 AND C.P_STATUS in ('8796105375835','8796105670747','8796105375835')
AND C.P_ALLOCATIONEMPLOYEECODE = #{distributorId}
AND ROWNUM between ${offset} and ${pageSize}

select C.PK, C.P_STATUS, OE.P_QUANTITY as P_QUANTITY, OE.P_PAYAMOUNT as p_payamount, AD.P_FIRSTNAME as P_FIRSTNAME, AD.P_PHONE1 as P_PHONE1, AD.P_STREETNAME as p_streetname, ROWNUM RN from CONSIGNMENTS C LEFT JOIN ORDERS O ON O.PK=C.P_ORDER LEFT JOIN O2OCONSIGNMENTITEMS O2O ON C.PK=O2O.P_CONSIGNMENT LEFT JOIN ORDERENTRIES OE ON OE.P_ORDER=O.PK LEFT JOIN ADDRESSES AD ON C.P_SHIPPINGADDRESS=AD.PK where 1= 1 AND C.P_STATUS = '8796105637979'
AND O2O.P_RECOMMENDSTORE = #{storeId}
AND ROWNUM between ${offset} and ${pageSize}

mapper.update:
