mapper.select:
select SO.PK, SO.P_SALESLEADS, SO.P_AGENCY, SO.P_SALESLEADSORDERSTATUS, SO.P_RESOLVED, SO.P_DISTRIBUTEDATE, SO.P_DISTRIBUTER, SO.P_AGENCYACCEPTDATE, A.P_NAME AS P_AGENCYACCEPT, SO.P_REFUSEDATE, SO.P_SUGGESTSTORE, SO.P_CUSADDRESS, SO.P_ACCEPTSTORE, SO.P_ACCEPTBY, SO.P_ACCEPTDATE, SO.P_APPOINTMENTDATE, SO.P_ACTUALDATE, SO.P_AMOUNT, SO.P_ORDERPICTURE, SO.P_TELEPHONE, SO.P_NAME, SO.P_FITMENTDATE, SO.P_STYLEPREFERENCE, SO.P_COMMENT, SO.P_BRANDPREFERENCE, SO.P_BUDGET, SO.P_HANDLER, SO.P_HANDLERCOMMENT, SO.P_CLOSEDATE, SO.P_CLOSEREASON, SO.P_REFUSEDATE, SO.P_REFUSETYPE, SO.P_REFUSEREASON, SO.P_SERVERADDRESS, SO.P_SERVERPURPOSE, SO.P_SERVICETITLE, SO.P_CALLSERVICETEL, SO.P_AGENCYOPERATEDATE, SO.P_STOREACCEPTORREMARK, ROWNUM RN from SALESLEADSORDER SO LEFT JOIN AGENCY A on A.PK=SO.P_AGENCY where 1= 1 AND SO.P_SALESLEADSORDERSTATUS='0'
AND SO.P_DISTRIBUTER = ${agency}
AND ROWNUM between ${startNum} and ${pageSize}

select PK, P_SALESLEADS, P_AGENCY, P_SALESLEADSORDERSTATUS, P_RESOLVED, P_DISTRIBUTEDATE, P_DISTRIBUTER, P_AGENCYACCEPTDATE, P_AGENCYACCEPT, P_REFUSEDATE, P_SUGGESTSTORE, P_CUSADDRESS, P_ACCEPTSTORE, P_ACCEPTBY, P_ACCEPTDATE, P_APPOINTMENTDATE, P_ACTUALDATE, P_AMOUNT, P_ORDERPICTURE, P_TELEPHONE, P_NAME, P_FITMENTDATE, P_STYLEPREFERENCE, P_COMMENT, P_BRANDPREFERENCE, P_BUDGET, P_HANDLER, P_HANDLERCOMMENT, P_CLOSEDATE, P_CLOSEREASON, P_REFUSEDATE, P_REFUSETYPE, P_REFUSEREASON, P_SERVERADDRESS, P_SERVERPURPOSE, P_SERVICETITLE, P_CALLSERVICETEL, P_AGENCYOPERATEDATE, P_STOREACCEPTORREMARK, ROWNUM RN from SALESLEADSORDER where 1= 1 AND P_SALESLEADSORDERSTATUS in ('10','15','20','23')
AND P_DISTRIBUTER = #{distributorId}
AND ROWNUM between ${startNum} and ${pageSize}

select PK, P_NAME, P_TELEPHONE, P_CUSADDRESS, P_COMMENT, P_SERVERADDRESS, P_SERVERPURPOSE, P_SERVICETITLE, P_FITMENTDATE, P_STYLEPREFERENCE, P_BUDGET, P_BRANDPREFERENCE, P_HANDLERCOMMENT, P_SALESLEADSORDERSTATUS, P_DISTRIBUTEDATE, P_DISTRIBUTER, P_AGENCYOPERATEDATE, P_AGENCYACCEPT, P_SUGGESTSTORE, P_ACCEPTDATE, P_APPOINTMENTDATE, P_CLOSEDATE, P_AMOUNT, P_CALLSERVICETEL, P_REFUSEDATE, P_REFUSETYPE, P_REFUSEREASON, P_AGENCYREMARK from SALESLEADSORDER where 1= 1 AND P_SALESLEADSORDERSTATUS='10'
and P_SUGGESTSTORE=${storeId}
and ${startNum} < rownum and rownum < ${pageSize}+${startNum}+1

select SO.PK, SO.P_NAME, SO.P_TELEPHONE, SO.P_CUSADDRESS, SO.P_COMMENT, SO.P_SERVERADDRESS, SO.P_SERVERPURPOSE, SO.P_SERVICETITLE, SO.P_FITMENTDATE, SO.P_STYLEPREFERENCE, SO.P_BUDGET, SO.P_BRANDPREFERENCE, SO.P_HANDLERCOMMENT, SO.P_SALESLEADSORDERSTATUS, SO.P_DISTRIBUTEDATE, SO.P_DISTRIBUTER, SO.P_AGENCYOPERATEDATE, SO.P_AGENCYACCEPT, SO.P_SUGGESTSTORE, SO.P_ACCEPTDATE, SO.P_APPOINTMENTDATE, SO.P_CLOSEDATE, SO.P_AMOUNT, SO.P_CALLSERVICETEL, SO.P_REFUSEDATE, SO.P_REFUSETYPE, SO.P_REFUSEREASON, SO.P_AGENCYREMARK from SALESLEADSORDER SO
, POINTOFSERVICE PS
where 1= 1 AND SO.P_SALESLEADSORDERSTATUS in ('25','30')
and SO.P_DISTRIBUTER=${distributorId}
and SO.P_DISTRIBUTEDATE between #{distributeTime.begin} and #{distributeTime.end}
and SO.P_ACCEPTSTORE=#{storeId}
and SO.P_SALESLEADS=${saleLeadId}
and SO.P_NAME like '%${clientName}%'
and SO.P_TELEPHONE=${clientTel}
and SO.p_acceptby=${acceptorId}
and PS.P_NAME like '%${storeName}%' and SO.P_ACCEPTSTORE = PS.PK
and ${startNum} < rownum and rownum < ${pageSize}+${startNum}+1

select * from (

left join agency a on s.p_agency = a .pk where  P_SALESLEADSORDERSTATUS in ('15','20','23')

	and s.p_acceptby=${acceptorId}
 and  rownum <${startNum}+${pageSize}+1) P1 where P1.rn>${startNum}

select * from (

left join agency a on s.p_agency = a .pk where  p_salesleadsorderstatus='25'

	and s.p_acceptby=${acceptorId}
 and rownum <${startNum}+${pageSize}+1) P1 where P1.rn>${startNum}
